// src/components/RelationshipDiagram.js

import React, { useState, useEffect } from 'react';
import Mermaid from 'react-mermaid2';

const RelationshipDiagram = ({ characters }) => {
    const [diagramCode, setDiagramCode] = useState('');

    useEffect(() => {
        if (characters.length > 0) {
            let code = 'graph TD\n';
            // Add all characters as nodes
            characters.forEach((char) => {
                code += `${char.name.replace(/\s+/g, '_')}["${char.name}"]\n`;
            });
            // Add relationships
            characters.forEach((char) => {
                if (char.relationships) {
                    char.relationships.forEach((rel) => {
                        code += `${char.name.replace(/\s+/g, '_')} --- |"${rel.relationship}"| ${rel.name
                            .replace(/\s+/g, '_')}\n`;
                    });
                }
            });
            setDiagramCode(code);
        }
    }, [characters]);

    return (
        <div className="border rounded-lg p-4 bg-white mt-6">
            <h3 className="text-lg font-medium mb-4 text-[#f79321]">Character Relationships</h3>
            <Mermaid chart={diagramCode} />
        </div>
    );
};

export default RelationshipDiagram;
