import React, { useState, useEffect, useRef } from 'react';
import { Loader2, AlertCircle, TerminalIcon } from 'lucide-react'; // Changed Terminal to TerminalIcon
import CharacterViewer from './CharacterViewer';
import mermaid from 'mermaid';

// Initialize mermaid with custom styling
mermaid.initialize({
    startOnLoad: true,
    theme: 'default',
    securityLevel: 'loose',
    themeVariables: {
        primaryColor: '#f79321',
        primaryTextColor: '#fff',
        primaryBorderColor: '#f79321',
        lineColor: '#f79321',
        secondaryColor: '#fff',
        tertiaryColor: '#fff'
    }
});

// Separate Mermaid diagram component with useRef
const RelationshipDiagram = ({ characters }) => {
    const diagramRef = useRef(null);

    useEffect(() => {
        const generateDiagram = async () => {
            if (diagramRef.current && characters.length > 0) {
                try {
                    let graphDefinition = 'graph TD\n';
                    characters.forEach(char => {
                        graphDefinition += `${char.name.replace(/\s+/g, '_')}["${char.name}"]\n`;
                    });

                    characters.forEach(char => {
                        if (char.relationships) {
                            char.relationships.forEach(rel => {
                                graphDefinition += `${char.name.replace(/\s+/g, '_')} --- |"${rel.relationship}"| ${rel.name.replace(/\s+/g, '_')}\n`;
                            });
                        }
                    });

                    const id = 'mermaid-' + Math.random().toString(36).substr(2, 9);
                    const { svg } = await mermaid.render(id, graphDefinition);
                    diagramRef.current.innerHTML = svg;
                } catch (error) {
                    console.error('Mermaid rendering error:', error);
                }
            }
        };

        generateDiagram();
    }, [characters]);

    return (
        <div className="mt-8 p-4 bg-white rounded-lg shadow">
            <h2 className="text-xl font-bold text-[#f79321] mb-4">Character Relationships</h2>
            <div ref={diagramRef} className="overflow-x-auto"></div>
        </div>
    );
};

// Update the GenerationProgress component to use TerminalIcon
const GenerationProgress = ({ progress, currentStep, logs }) => {
    const logsEndRef = useRef(null);

    // Auto-scroll to bottom when new logs arrive
    useEffect(() => {
        if (logsEndRef.current) {
            logsEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [logs]);

    return (
        <div className="space-y-4 bg-white rounded-lg p-4 border">
            {/* Progress Bar */}
            <div className="space-y-2">
                <div className="h-2 w-full bg-gray-200 rounded-full">
                    <div
                        className="h-2 bg-[#f79321] rounded-full transition-all duration-500"
                        style={{ width: `${progress}%` }}
                    />
                </div>
                <p className="text-sm text-gray-600">{currentStep}</p>
            </div>

            {/* Log Display */}
            <div className="mt-4 border rounded-lg p-4 bg-gray-50">
                <div className="flex items-center gap-2 mb-2">
                    <TerminalIcon className="h-4 w-4 text-[#f79321]" />
                    <h3 className="font-medium">Generation Log</h3>
                </div>
                <div className="h-48 overflow-y-auto font-mono text-sm space-y-1 p-2">
                    {logs.length === 0 ? (
                        <div className="text-gray-500 italic">Waiting for generation to start...</div>
                    ) : (
                        logs.map((log, index) => (
                            <div key={index} className="text-gray-700">
                                {log}
                            </div>
                        ))
                    )}
                    <div ref={logsEndRef} />
                </div>
            </div>
        </div>
    );
};

const CharacterGenerator = () => {
    // Initialize all state variables
    const [formData, setFormData] = useState({
        apiKey: '',
        loreText: '',
        namesText: '',
        numCharacters: 5,
        temperature: 0.7,
    });
    const [isGenerating, setIsGenerating] = useState(false);
    const [error, setError] = useState('');
    const [characters, setCharacters] = useState([]);
    const [progress, setProgress] = useState(0);
    const [currentStep, setCurrentStep] = useState('');
    const [logs, setLogs] = useState([]);


    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Add WebSocket connection
    // In CharacterGenerator.js, update the WebSocket useEffect
    useEffect(() => {
        let ws;
        try {
            ws = new WebSocket('ws://localhost:3002');

            ws.onopen = () => {
                console.log('WebSocket Connected');
                setLogs(prev => [...prev, "WebSocket Connected - Ready to receive updates"]);
            };

            ws.onmessage = (event) => {
                console.log('Received message:', event.data); // Debug log
                const data = JSON.parse(event.data);

                if (data.type === 'progress') {
                    setProgress(data.progress);
                    setCurrentStep(data.step);
                } else if (data.type === 'log') {
                    setLogs(prev => {
                        console.log('Adding log:', data.message); // Debug log
                        return [...prev, data.message];
                    });
                }
            };

            ws.onerror = (error) => {
                console.error('WebSocket error:', error);
                setLogs(prev => [...prev, `WebSocket Error: ${error.message}`]);
            };

            ws.onclose = () => {
                console.log('WebSocket Disconnected');
                setLogs(prev => [...prev, "WebSocket Disconnected"]);
            };

        } catch (error) {
            console.error('WebSocket connection error:', error);
            setLogs(prev => [...prev, `WebSocket Connection Error: ${error.message}`]);
        }

        return () => {
            if (ws) {
                ws.close();
            }
        };
    }, []);

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate that we have enough names
        const names = formData.namesText
            .split('\n')
            .map(name => name.trim())
            .filter(name => name.length > 0);

        if (names.length < formData.numCharacters) {
            setError('Not enough character names provided. Please add more names.');
            return;
        }

        setError('');
        setIsGenerating(true);
        setProgress(0);
        setCurrentStep('');
        setLogs([]);

        try {
            const response = await fetch('http://localhost:3001/api/generate', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({
                    ...formData,
                    namesText: names.join('\n') // Send cleaned names
                }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'An error occurred');
            }

            const data = await response.json();
            setCharacters(data.characters);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsGenerating(false);
        }
    };

    return (
        <div className="min-h-screen bg-[#0a1929] text-[#f79321] p-6 terminal-container">
            <div className="scanline" />
            <div className="max-w-6xl mx-auto space-y-8">
                {/* Header with ASCII art and description */}
                <div className="space-y-4 font-mono glitch-text">
                    <pre className="text-[#f79321] text-lg whitespace-pre font-mono">
                        {`
                         .---.                          
           __.....__     |   |.--.                      
       .-''         '.   |   ||__|                      
      /     .-''"'-.  '. |   |.--.                      
     /     /________\\   \\|   ||  |               __     
     |                  ||   ||  |.--------.  .:--.'.   
     \\    .-------------'|   ||  ||____    | / |   \\ |  
      \\    '-.____...---.|   ||  |    /   /  \`" __ | |  
       \`.             .' |   ||__|  .'   /    .'.''| |  
         \`''-...... -'   '---'     /    /___ / /   | |_ 
                                  |         |\\ \\._,\\ '/ 
                                  |_________| \`--'  \`"  
    `}
                    </pre>
                    <div className="bg-black/30 p-4 rounded-lg border border-[#f79321]/30">
                        <TypingText
                            text="eliza is a lightweight AI agent framework. It leverages Character files - JSON-formatted configurations that define an AI character's personality, knowledge, and behavior patterns. This tool enables you to develop a set of Character files based on your own lore, and connects the narratives of multiple agents together through their character files."
                            className="text-[#00ff00]"
                            speed={30}
                        />
                    </div>
                </div>

                {/* Input Form */}
                <form onSubmit={handleSubmit} className="space-y-6">
                    {/* World Lore Section */}
                    <div className="space-y-2 terminal-text visible">
                        <div className="flex items-center gap-2 text-[#f79321]">
                            <TerminalIcon className="h-4 w-4" />
                            <h2 className="font-mono text-lg glitch-text">World Lore</h2>
                        </div>
                        <p className="text-[#00ff00]/70 text-sm">
                            <TypingText
                                text="Define your world's background story, rules, and setting. This context will shape your characters' personalities and relationships."
                                speed={20}
                            />
                        </p>
                        <textarea
                            name="loreText"
                            value={formData.loreText}
                            onChange={handleInputChange}
                            className="w-full h-40 bg-black/30 border border-[#f79321]/30 rounded-lg p-4 text-[#00ff00] font-mono focus:border-[#f79321] focus:ring-1 focus:ring-[#f79321] typing-effect"
                            placeholder="> Enter your world's lore here..."
                            required
                        />
                    </div>

                    {/* Character Names Section */}
                    <div className="space-y-2 terminal-text visible">
                        <div className="flex items-center gap-2 text-[#f79321]">
                            <TerminalIcon className="h-4 w-4" />
                            <h2 className="font-mono text-lg glitch-text">Character Names</h2>
                        </div>
                        <p className="text-[#00ff00]/70 text-sm">
                            <TypingText
                                text="List your character names (one per line). These will be developed into full character profiles."
                                speed={20}
                            />
                        </p>
                        <textarea
                            name="namesText"
                            value={formData.namesText}
                            onChange={handleInputChange}
                            className="w-full h-32 bg-black/30 border border-[#f79321]/30 rounded-lg p-4 text-[#00ff00] font-mono focus:border-[#f79321] focus:ring-1 focus:ring-[#f79321] typing-effect"
                            placeholder="> Enter character names (one per line)..."
                            required
                        />
                    </div>

                    {/* Configuration Settings */}
                    <div className="bg-black/30 border border-[#f79321]/30 rounded-lg p-4 space-y-4 terminal-text visible">
                        <div className="flex items-center gap-2 text-[#f79321]">
                            <TerminalIcon className="h-4 w-4" />
                            <h2 className="font-mono text-lg glitch-text">Configuration</h2>
                        </div>

                        {/* OpenAI Key */}
                        <div className="space-y-2">
                            <label className="block font-mono">
                                OpenAI API Key
                                <input
                                    type="password"
                                    name="apiKey"
                                    value={formData.apiKey}
                                    onChange={handleInputChange}
                                    className="mt-1 block w-full bg-black/30 border border-[#f79321]/30 rounded-lg px-4 py-2 text-[#00ff00] font-mono focus:border-[#f79321] focus:ring-1 focus:ring-[#f79321] typing-effect"
                                    required
                                />
                            </label>
                            <p className="text-xs text-[#00ff00]/70">
                                <TypingText
                                    text="Your API key will not be stored."
                                    speed={20}
                                />
                            </p>
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                            {/* Number of Characters */}
                            <div className="space-y-2">
                                <label className="block font-mono">
                                    Number of Characters
                                    <input
                                        type="number"
                                        name="numCharacters"
                                        value={formData.numCharacters}
                                        onChange={handleInputChange}
                                        min="1"
                                        max="10"
                                        className="mt-1 block w-full bg-black/30 border border-[#f79321]/30 rounded-lg px-4 py-2 text-[#00ff00] font-mono focus:border-[#f79321] focus:ring-1 focus:ring-[#f79321] typing-effect"
                                    />
                                </label>
                            </div>

                            {/* Temperature */}
                            <div className="space-y-2">
                                <label className="block font-mono">
                                    Temperature
                                    <input
                                        type="number"
                                        name="temperature"
                                        value={formData.temperature}
                                        onChange={handleInputChange}
                                        min="0"
                                        max="1"
                                        step="0.1"
                                        className="mt-1 block w-full bg-black/30 border border-[#f79321]/30 rounded-lg px-4 py-2 text-[#00ff00] font-mono focus:border-[#f79321] focus:ring-1 focus:ring-[#f79321] typing-effect"
                                    />
                                </label>
                            </div>
                        </div>
                    </div>

                    {/* Generate Button */}
                    <button
                        type="submit"
                        disabled={isGenerating}
                        className="w-full bg-black/30 border border-[#f79321] text-[#f79321] font-mono py-3 rounded-lg hover:bg-[#f79321]/20 disabled:opacity-50 glitch-text"
                    >
                        {isGenerating ? (
                            <span className="flex items-center justify-center gap-2">
                                <Loader2 className="h-4 w-4 animate-spin" />
                                <TypingText text="Generating Characters..." speed={50} />
                            </span>
                        ) : (
                            '> Generate Characters_'
                        )}
                    </button>
                </form>

                {/* Progress Display */}
                {isGenerating && (
                    <div className="terminal-text visible">
                        <GenerationProgress
                            progress={progress}
                            currentStep={currentStep}
                            logs={logs}
                        />
                    </div>
                )}

                {/* Results Display */}
                {characters.length > 0 && (
                    <>
                        <div className="space-y-4 terminal-text visible">
                            <div className="flex items-center gap-2 text-[#f79321]">
                                <TerminalIcon className="h-4 w-4" />
                                <h2 className="font-mono text-lg glitch-text">Generated Characters</h2>
                            </div>
                            {characters.map((character, index) => (
                                <CharacterViewer key={index} character={character} />
                            ))}
                        </div>
                        <RelationshipDiagram characters={characters} />
                    </>
                )}
            </div>
        </div>
    );
}

export default CharacterGenerator;
