// src/components/CharacterGenerator.js
import React, { useState, useEffect, useRef } from 'react';
import { Loader2, AlertCircle } from 'lucide-react';
import mermaid from 'mermaid';
import CharacterViewer from './CharacterViewer';

// Initialize mermaid with custom styling
mermaid.initialize({
    startOnLoad: true,
    theme: 'default',
    securityLevel: 'loose',
    themeVariables: {
        primaryColor: '#f79321',
        primaryTextColor: '#fff',
        primaryBorderColor: '#f79321',
        lineColor: '#f79321',
        secondaryColor: '#fff',
        tertiaryColor: '#fff'
    }
});

// Separate Mermaid diagram component with useRef
const RelationshipDiagram = ({ characters }) => {
    const diagramRef = useRef(null);

    useEffect(() => {
        const generateDiagram = async () => {
            if (diagramRef.current && characters.length > 0) {
                try {
                    let graphDefinition = 'graph TD\n';
                    characters.forEach(char => {
                        graphDefinition += `${char.name.replace(/\s+/g, '_')}["${char.name}"]\n`;
                    });

                    characters.forEach(char => {
                        if (char.relationships) {
                            char.relationships.forEach(rel => {
                                graphDefinition += `${char.name.replace(/\s+/g, '_')} --- |"${rel.relationship}"| ${rel.name.replace(/\s+/g, '_')}\n`;
                            });
                        }
                    });

                    const id = 'mermaid-' + Math.random().toString(36).substr(2, 9);
                    const { svg } = await mermaid.render(id, graphDefinition);
                    diagramRef.current.innerHTML = svg;
                } catch (error) {
                    console.error('Mermaid rendering error:', error);
                }
            }
        };

        generateDiagram();
    }, [characters]);

    return (
        <div className="mt-8 p-4 bg-white rounded-lg shadow">
            <h2 className="text-xl font-bold text-[#f79321] mb-4">Character Relationships</h2>
            <div ref={diagramRef} className="overflow-x-auto"></div>
        </div>
    );
};

const CharacterGenerator = () => {
    const [formData, setFormData] = useState({
        apiKey: '',
        loreText: '',
        namesText: '',
        numCharacters: 5,
        temperature: 0.7,
    });

    const [isGenerating, setIsGenerating] = useState(false);
    const [error, setError] = useState('');
    const [characters, setCharacters] = useState([]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setIsGenerating(true);

        try {
            const response = await fetch('http://localhost:3001/api/generate', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'An error occurred');
            }

            const data = await response.json();
            setCharacters(data.characters);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsGenerating(false);
        }
    };

    return (
        <div className="min-h-screen bg-orange-50">
            <div className="max-w-4xl mx-auto p-6 space-y-8">
                {/* Header */}
                <div className="space-y-4">
                    <h1 className="text-3xl font-bold text-[#f79321]">Character Generator</h1>
                    <p className="text-gray-600">Generate character profiles using AI</p>
                </div>

                {/* Error Alert */}
                {error && (
                    <div className="alert alert-error flex items-center gap-2 bg-red-100 p-4 rounded-md">
                        <AlertCircle className="h-5 w-5 text-red-600" />
                        <span className="text-red-600">{error}</span>
                    </div>
                )}

                <form onSubmit={handleSubmit} className="space-y-6">
                    {/* API Key Input */}
                    <div className="space-y-2">
                        <label className="block font-medium">
                            OpenAI API Key
                            <input
                                type="password"
                                name="apiKey"
                                value={formData.apiKey}
                                onChange={handleInputChange}
                                className="mt-1 block w-full rounded-md border px-3 py-2"
                                required
                            />
                        </label>
                        <p className="text-sm text-gray-500">Your API key will not be stored.</p>
                    </div>

                    {/* Lore Text Input */}
                    <div className="space-y-2">
                        <label className="block font-medium">
                            World Lore
                            <textarea
                                name="loreText"
                                value={formData.loreText}
                                onChange={handleInputChange}
                                className="mt-1 block w-full rounded-md border px-3 py-2 h-40"
                                placeholder="Enter your world's lore here..."
                                required
                            />
                        </label>
                    </div>

                    {/* Names Input */}
                    <div className="space-y-2">
                        <label className="block font-medium">
                            Character Names
                            <textarea
                                name="namesText"
                                value={formData.namesText}
                                onChange={handleInputChange}
                                className="mt-1 block w-full rounded-md border px-3 py-2 h-32"
                                placeholder="Enter character names (one per line)..."
                                required
                            />
                        </label>
                    </div>

                    {/* Advanced Settings */}
                    <div className="space-y-4 border rounded-lg p-4 bg-white">
                        <h3 className="font-medium">Advanced Settings</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            {/* Number of Characters */}
                            <div className="space-y-2">
                                <label className="block font-medium text-sm">
                                    Number of Characters
                                    <input
                                        type="number"
                                        name="numCharacters"
                                        value={formData.numCharacters}
                                        onChange={handleInputChange}
                                        min="1"
                                        max="10"
                                        className="mt-1 block w-full rounded-md border px-3 py-2"
                                    />
                                </label>
                            </div>
                            {/* Temperature */}
                            <div className="space-y-2">
                                <label className="block font-medium text-sm">
                                    Temperature
                                    <input
                                        type="number"
                                        name="temperature"
                                        value={formData.temperature}
                                        onChange={handleInputChange}
                                        min="0"
                                        max="1"
                                        step="0.1"
                                        className="mt-1 block w-full rounded-md border px-3 py-2"
                                    />
                                </label>
                            </div>
                        </div>
                    </div>

                    {/* Submit Button */}
                    <button
                        type="submit"
                        disabled={isGenerating}
                        className="w-full rounded-md bg-[#f79321] px-4 py-2 text-white font-medium hover:bg-[#e58420] disabled:bg-[#f7932180]"
                    >
                        {isGenerating ? (
                            <span className="flex items-center justify-center gap-2">
                                <Loader2 className="h-4 w-4 animate-spin" />
                                Generating Characters...
                            </span>
                        ) : (
                            'Generate Characters'
                        )}
                    </button>
                </form>

                {/* Character List and Relationship Diagram */}
                {characters.length > 0 && (
                    <>
                        <div className="space-y-4">
                            <h2 className="text-xl font-bold text-[#f79321]">Generated Characters</h2>
                            {characters.map((character, index) => (
                                <CharacterViewer key={index} character={character} />
                            ))}
                        </div>
                        <RelationshipDiagram characters={characters} />
                    </>
                )}
            </div>
        </div>
    );
};

export default CharacterGenerator;
